mapper.select:

select notice_id, notice_title, notice_reg_date
from (
    select rownum as rnum, notice_id, notice_title, notice_reg_date
    from (
        select notice_id, notice_title, notice_reg_date
        from notice
        order by notice_reg_date desc
    )
    where rownum <= #{endRowNo}
) 
where rnum >= #{startRowNo}
