mapper.select:

select notice_id, notice_title, notice_reg_date
from (
    select rownum as rnum, notice_id, notice_title, notice_reg_date
    from (
        select notice_id, notice_title, notice_reg_date
        from notice
        order by notice_reg_date desc
    )
    where rownum <= #{endRowNo}
) 
where rnum >= #{startRowNo}

select notice_title, notice_reg_date, notice_hitcount, notice_content
from notice
where notice_id = #{noticeId}

mapper.update:
update notice
set notice_hitcount = notice_hitcount + 1
where notice_id = #{noticeId}

mapper.insert:
insert into notice (
	notice_id, notice_writer, notice_title, notice_content, notice_reg_date, notice_hitcount
)
values (
	notice_seq.NEXTVAL, #{noticeWriter}, #{noticeTitle}, #{noticeContent}, sysdate, #{noticeHitcount}
)
