mapper.insert:
insert into cart (
	user_id, product_id, product_qty
) values (
	#{userId}, #{productId}, #{productQty}
)

mapper.select:
select count(*)
from cart
where user_id = #{userId}
and product_id = #{productId}

mapper.update:
update cart
set product_qty = #{productQty}
where user_id = #{userId}
and product_id = #{productId}

mapper.delete:
delete from cart
where user_id = #{userId}
and product_id = #{productId}

mapper.select:
select 
    user_id, product_id, product_qty,
    product_name, product_price, product_summary
from cart
join product
using(product_id)
where user_id = #{userId}
and product_id = #{productId}

