select order_num, order_id, user_id, order_date, order_total_price
from (
    select order_num, order_id, user_id, order_date, order_total_price
    from (
        select row_number() over (order by order_id desc) as order_num, order_id, user_id, order_date, order_total_price
        from orders
        where user_id = #{userId}
    )
