mapper.select:

select product_id, product_name, product_price, product_summary, category, product_reg_date, product_stock
from(
	select rownum rnum, product_id, product_name, product_price, product_summary, category, product_reg_date, product_stock
	from (
		select product_id, product_name, product_price, product_summary, category, product_reg_date, product_stock
		from product
		order by product_id desc
	)
	where rownum <= #{endRowNo}
)
where rnum >= #{startRowNo} 


		order by product_reg_date desc


		order by product_price asc


		order by product_price desc

select count(*) from product
where category=#{category}
where product_name like '%'|| #{searchContent}|| '%'

select PRODUCT_ID,  PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_SUMMARY, PRODUCT_DETAIL_SUMMARY, CATEGORY 
from product
where product_Id = #{productId}

mapper.insert:
insert into product (
	product_id, product_name, product_price, product_stock, product_summary,
	product_detail_summary, product_reg_date, category
	)
values(
	199, #{productName}, #{productPrice}, #{productStock}, #{productSummary},
	#{productDetailSummary}, sysdate, #{category}
	)

mapper.select:
select product_id
from product
where product_name = #{productName}
