mapper.insert:
insert into wishlist (
	wishlist_id, user_id, product_id
)
values (
	wishlist_seq.nextval, #{userId}, #{productId}
)

mapper.select:
select wishlist_id
from wishlist
where user_id = #{userId}
	and product_id = #{productId}

mapper.delete:
delete from wishlist
where user_id = #{userId}
	and product_id = #{productId}

mapper.select:
		select 
    		product_id, product_name, product_price, product_summary
		from (
    		select 
        		rownum AS rnum, product_id, product_name, product_price, product_summary
    		from (
        		select product_id, product_name, product_price, product_summary
        		from wishlist
        		join product USING (product_id)
        		order by wishlist_id
    		)
		)
		where rnum between #{startRowNo} and #{endRowNo}

select count(*)
from wishlist
where user_id = #{userId}

select product_id
from wishlist
where user_id = #{userId}
