mapper.insert:
insert into wishlist (
	wishlist_id, user_id, product_id
)
values (
	wishlist_seq.nextval, #{userId}, #{productId}
)

mapper.select:
select wishlist_id
from wishlist
where user_id = #{userId}
	and product_id = #{productId}

mapper.delete:
delete from wishlist
where user_id = #{userId}
	and product_id = #{productId}

mapper.select:
select product_id
from wishlist
where user_id = #{userId}
